import{_ as e,c as s,o as a,a as n}from"./app.80833568.js";const f=JSON.parse('{"title":"CLI Commands","description":"","frontmatter":{"title":"CLI Commands"},"headers":[{"level":2,"title":"Command Overview","slug":"command-overview","link":"#command-overview","children":[]},{"level":2,"title":"loadpulse init","slug":"loadpulse-init","link":"#loadpulse-init","children":[{"level":3,"title":"Usage","slug":"usage","link":"#usage","children":[]},{"level":3,"title":"Description","slug":"description","link":"#description","children":[]},{"level":3,"title":"Example","slug":"example","link":"#example","children":[]},{"level":3,"title":"When to Use","slug":"when-to-use","link":"#when-to-use","children":[]},{"level":3,"title":"Output","slug":"output","link":"#output","children":[]}]},{"level":2,"title":"loadpulse validate","slug":"loadpulse-validate","link":"#loadpulse-validate","children":[{"level":3,"title":"Usage","slug":"usage-1","link":"#usage-1","children":[]},{"level":3,"title":"Arguments","slug":"arguments","link":"#arguments","children":[]},{"level":3,"title":"Description","slug":"description-1","link":"#description-1","children":[]},{"level":3,"title":"Examples","slug":"examples","link":"#examples","children":[]},{"level":3,"title":"When to Use","slug":"when-to-use-1","link":"#when-to-use-1","children":[]},{"level":3,"title":"Output","slug":"output-1","link":"#output-1","children":[]}]},{"level":2,"title":"loadpulse run","slug":"loadpulse-run","link":"#loadpulse-run","children":[{"level":3,"title":"Usage","slug":"usage-2","link":"#usage-2","children":[]},{"level":3,"title":"Options","slug":"options","link":"#options","children":[]},{"level":3,"title":"Description","slug":"description-2","link":"#description-2","children":[]},{"level":3,"title":"Examples","slug":"examples-1","link":"#examples-1","children":[]},{"level":3,"title":"Prerequisites","slug":"prerequisites","link":"#prerequisites","children":[]},{"level":3,"title":"When to Use","slug":"when-to-use-2","link":"#when-to-use-2","children":[]},{"level":3,"title":"Output","slug":"output-2","link":"#output-2","children":[]}]},{"level":2,"title":"loadpulse clean","slug":"loadpulse-clean","link":"#loadpulse-clean","children":[{"level":3,"title":"Usage","slug":"usage-3","link":"#usage-3","children":[]},{"level":3,"title":"Description","slug":"description-3","link":"#description-3","children":[]},{"level":3,"title":"Examples","slug":"examples-2","link":"#examples-2","children":[]},{"level":3,"title":"When to Use","slug":"when-to-use-3","link":"#when-to-use-3","children":[]},{"level":3,"title":"Output","slug":"output-3","link":"#output-3","children":[]}]},{"level":2,"title":"loadpulse version","slug":"loadpulse-version","link":"#loadpulse-version","children":[{"level":3,"title":"Usage","slug":"usage-4","link":"#usage-4","children":[]},{"level":3,"title":"Description","slug":"description-4","link":"#description-4","children":[]},{"level":3,"title":"Example","slug":"example-1","link":"#example-1","children":[]}]},{"level":2,"title":"Getting Help","slug":"getting-help","link":"#getting-help","children":[]},{"level":2,"title":"Common Workflows","slug":"common-workflows","link":"#common-workflows","children":[{"level":3,"title":"First-Time Setup","slug":"first-time-setup","link":"#first-time-setup","children":[]},{"level":3,"title":"Running Multiple Tests","slug":"running-multiple-tests","link":"#running-multiple-tests","children":[]},{"level":3,"title":"Troubleshooting","slug":"troubleshooting","link":"#troubleshooting","children":[]}]}],"relativePath":"commands.md"}'),l={name:"commands.md"},i=n(`<h1 id="cli-commands-and-usage" tabindex="-1">CLI Commands and Usage <a class="header-anchor" href="#cli-commands-and-usage" aria-hidden="true">#</a></h1><p>This page documents all Load-Pulse CLI commands, their options, and example usage.</p><p>All examples assume <code>loadpulse</code> is installed and available on your <code>PATH</code>. If you&#39;re running from source, replace <code>loadpulse</code> with <code>go run ./main.go</code> (or <code>go run .\\main.go</code> on Windows).</p><h2 id="command-overview" tabindex="-1">Command Overview <a class="header-anchor" href="#command-overview" aria-hidden="true">#</a></h2><p>Load-Pulse provides the following commands:</p><ul><li><a href="#loadpulse-init"><code>loadpulse init</code></a> \u2013 Create a test configuration file interactively</li><li><a href="#loadpulse-validate"><code>loadpulse validate</code></a> \u2013 Validate a test configuration file</li><li><a href="#loadpulse-run"><code>loadpulse run</code></a> \u2013 Execute a load test</li><li><a href="#loadpulse-clean"><code>loadpulse clean</code></a> \u2013 Clean up Docker containers</li><li><a href="#loadpulse-version"><code>loadpulse version</code></a> \u2013 Display version information</li></ul><h2 id="loadpulse-init" tabindex="-1"><code>loadpulse init</code> <a class="header-anchor" href="#loadpulse-init" aria-hidden="true">#</a></h2><p>Interactively create a <code>testConfig.json</code> configuration file.</p><h3 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">loadpulse init</span></span>
<span class="line"></span></code></pre></div><h3 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-hidden="true">#</a></h3><p>The <code>init</code> command launches an interactive wizard that guides you through creating a test configuration file. It prompts you for:</p><ul><li>Output file path (defaults to <code>testConfig.json</code> in the current directory)</li><li>Base host URL for your API</li><li>Test duration (in seconds)</li><li>Request definitions (method, endpoint, data, connections, rate, concurrency limit)</li></ul><h3 id="example" tabindex="-1">Example <a class="header-anchor" href="#example" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">$ loadpulse init</span></span>
<span class="line"><span style="color:#A6ACCD;">Enter output JSON file path </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">testConfig.json</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">: </span></span>
<span class="line"><span style="color:#A6ACCD;">This wizard will create a testConfig configuration file at: </span></span>
<span class="line"><span style="color:#A6ACCD;">testConfig.json</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Follow the interactive prompts...</span></span>
<span class="line"></span></code></pre></div><h3 id="when-to-use" tabindex="-1">When to Use <a class="header-anchor" href="#when-to-use" aria-hidden="true">#</a></h3><p>Use <code>loadpulse init</code> when:</p><ul><li>Setting up a new load test configuration</li><li>You want a guided, interactive way to create <code>testConfig.json</code></li><li>You&#39;re unfamiliar with the <code>testConfig.json</code> structure</li></ul><p>For manual configuration, see the <a href="/Load-Pulse/config-reference.html">Config Reference</a> page.</p><h3 id="output" tabindex="-1">Output <a class="header-anchor" href="#output" aria-hidden="true">#</a></h3><p>The command creates a <code>testConfig.json</code> file (or the path you specified) in the current directory with your configuration.</p><hr><h2 id="loadpulse-validate" tabindex="-1"><code>loadpulse validate</code> <a class="header-anchor" href="#loadpulse-validate" aria-hidden="true">#</a></h2><p>Check that a test configuration file is well-formed and complete.</p><h3 id="usage-1" tabindex="-1">Usage <a class="header-anchor" href="#usage-1" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">loadpulse validate </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">testConfig-file</span><span style="color:#89DDFF;">]</span></span>
<span class="line"></span></code></pre></div><h3 id="arguments" tabindex="-1">Arguments <a class="header-anchor" href="#arguments" aria-hidden="true">#</a></h3><ul><li><code>testConfig-file</code> (optional) \u2013 Path to the test configuration JSON file. Defaults to <code>testConfig.json</code> in the current directory if not specified.</li></ul><h3 id="description-1" tabindex="-1">Description <a class="header-anchor" href="#description-1" aria-hidden="true">#</a></h3><p>The <code>validate</code> command checks:</p><ul><li>That the file exists and is readable</li><li>That the JSON is valid and properly formatted</li><li>That all required fields are present</li><li>That field types and values are correct</li></ul><h3 id="examples" tabindex="-1">Examples <a class="header-anchor" href="#examples" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki has-diff"><code><span class="line"><span style="color:#676E95;"># Validate the default testConfig.json</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse validate</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Validate a specific file</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse validate path/to/testConfig.json</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Validate a file in a different directory</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse validate ../my-tests/api-test.json</span></span>
<span class="line"></span></code></pre></div><h3 id="when-to-use-1" tabindex="-1">When to Use <a class="header-anchor" href="#when-to-use-1" aria-hidden="true">#</a></h3><p>Use <code>loadpulse validate</code> when:</p><ul><li>Before running a long load test to catch configuration errors early</li><li>After manually editing <code>testConfig.json</code></li></ul><h3 id="output-1" tabindex="-1">Output <a class="header-anchor" href="#output-1" aria-hidden="true">#</a></h3><p>On success:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">[INFO]: Validating test configuration file: testConfig.json</span></span>
<span class="line"><span style="color:#A6ACCD;">[INFO]: testConfig configuration is valid: testConfig.json</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>On failure, the command will display a error messages.</p><hr><h2 id="loadpulse-run" tabindex="-1"><code>loadpulse run</code> <a class="header-anchor" href="#loadpulse-run" aria-hidden="true">#</a></h2><p>Run a load test using the given configuration file.</p><h3 id="usage-2" tabindex="-1">Usage <a class="header-anchor" href="#usage-2" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">loadpulse run --config </span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">path-to-testConfig.json</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre></div><h3 id="options" tabindex="-1">Options <a class="header-anchor" href="#options" aria-hidden="true">#</a></h3><ul><li><code>--config</code>, <code>-c</code> \u2013 Path to the test configuration JSON file (default: <code>testConfig.json</code>)</li></ul><h3 id="description-2" tabindex="-1">Description <a class="header-anchor" href="#description-2" aria-hidden="true">#</a></h3><p>The <code>run</code> command:</p><ol><li>Validates that Docker is running</li><li>Reads the test configuration file</li><li>Spins up Docker containers (load tester, aggregator, Redis, RabbitMQ)</li><li>Executes the load test according to your configuration</li><li>Displays aggregated results from the aggregator</li><li>Automatically cleans up containers after completion</li></ol><h3 id="examples-1" tabindex="-1">Examples <a class="header-anchor" href="#examples-1" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki has-diff"><code><span class="line"><span style="color:#676E95;"># Run with default testConfig.json</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Run with explicit config file</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run --config testConfig.json</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Run with config file using short flag</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run -c my-test.json</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Run with config in a different directory</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run --config ../tests/api-load-test.json</span></span>
<span class="line"></span></code></pre></div><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-hidden="true">#</a></h3><ul><li>Docker must be running (Docker Desktop or Docker daemon)</li><li>A valid <code>testConfig.json</code> file (use <code>loadpulse validate</code> to check)</li></ul><h3 id="when-to-use-2" tabindex="-1">When to Use <a class="header-anchor" href="#when-to-use-2" aria-hidden="true">#</a></h3><p>Use <code>loadpulse run</code> when:</p><ul><li>You&#39;re ready to execute a load test</li><li>You&#39;ve validated your configuration</li><li>Your target API server is running and accessible</li></ul><h3 id="output-2" tabindex="-1">Output <a class="header-anchor" href="#output-2" aria-hidden="true">#</a></h3><p>During execution, you&#39;ll see:</p><ul><li>Initialization messages</li><li>Docker container startup status</li><li>A spinner indicating test progress</li><li>Final aggregated statistics for each endpoint</li></ul><p>For details on interpreting results, see the <a href="/Load-Pulse/results.html">Results documentation</a>.</p><hr><h2 id="loadpulse-clean" tabindex="-1"><code>loadpulse clean</code> <a class="header-anchor" href="#loadpulse-clean" aria-hidden="true">#</a></h2><p>Stop and clean up Docker containers created by Load-Pulse.</p><h3 id="usage-3" tabindex="-1">Usage <a class="header-anchor" href="#usage-3" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">loadpulse clean</span></span>
<span class="line"></span></code></pre></div><h3 id="description-3" tabindex="-1">Description <a class="header-anchor" href="#description-3" aria-hidden="true">#</a></h3><p>The <code>clean</code> command:</p><ul><li>Stops all containers started by Load-Pulse</li><li>Removes containers and associated volumes</li><li>Cleans up resources from previous test runs</li></ul><h3 id="examples-2" tabindex="-1">Examples <a class="header-anchor" href="#examples-2" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># Clean up containers</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse clean</span></span>
<span class="line"></span></code></pre></div><h3 id="when-to-use-3" tabindex="-1">When to Use <a class="header-anchor" href="#when-to-use-3" aria-hidden="true">#</a></h3><p>Use <code>loadpulse clean</code> when:</p><ul><li>Containers from a previous run didn&#39;t shut down properly</li><li>You want to free up Docker resources</li><li>You&#39;re troubleshooting container-related issues</li></ul><p><strong>Note:</strong> The <code>loadpulse run</code> command automatically cleans up containers after a successful test run, so manual cleanup is usually not necessary.</p><h3 id="output-3" tabindex="-1">Output <a class="header-anchor" href="#output-3" aria-hidden="true">#</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">[INFO]: Starting cleanup process ...</span></span>
<span class="line"><span style="color:#A6ACCD;">[INFO]: Container Cleanup Successfully Completed</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>If no containers are found:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">[INFO]: No containers found to clean up</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><hr><h2 id="loadpulse-version" tabindex="-1"><code>loadpulse version</code> <a class="header-anchor" href="#loadpulse-version" aria-hidden="true">#</a></h2><p>Print the installed Load-Pulse version.</p><h3 id="usage-4" tabindex="-1">Usage <a class="header-anchor" href="#usage-4" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">loadpulse version</span></span>
<span class="line"></span></code></pre></div><h3 id="description-4" tabindex="-1">Description <a class="header-anchor" href="#description-4" aria-hidden="true">#</a></h3><p>Displays the version number of the installed Load-Pulse CLI.</p><h3 id="example-1" tabindex="-1">Example <a class="header-anchor" href="#example-1" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">$ loadpulse version</span></span>
<span class="line"><span style="color:#A6ACCD;">Load Pulse version: 1.0.0</span></span>
<span class="line"></span></code></pre></div><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-hidden="true">#</a></h2><p>For help with any command, use the <code>--help</code> flag:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">loadpulse --help</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse init --help</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run --help</span></span>
<span class="line"><span style="color:#676E95;"># etc.</span></span>
<span class="line"></span></code></pre></div><h2 id="common-workflows" tabindex="-1">Common Workflows <a class="header-anchor" href="#common-workflows" aria-hidden="true">#</a></h2><h3 id="first-time-setup" tabindex="-1">First-Time Setup <a class="header-anchor" href="#first-time-setup" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># 1. Create a test configuration</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse init</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 2. Validate it</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse validate</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 3. Run the test</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run</span></span>
<span class="line"></span></code></pre></div><h3 id="running-multiple-tests" tabindex="-1">Running Multiple Tests <a class="header-anchor" href="#running-multiple-tests" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki has-diff"><code><span class="line"><span style="color:#676E95;"># Create and validate first test</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse init</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse validate</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Run first test</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run --config testConfig.json</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Create second test configuration</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse init  </span><span style="color:#676E95;"># Specify different filename when prompted</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Run second test</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run --config testConfig-2.json</span></span>
<span class="line"></span></code></pre></div><h3 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># Check version</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse version</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Validate configuration</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse validate testConfig.json</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Clean up if containers are stuck</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse clean</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># Run test again</span></span>
<span class="line"><span style="color:#A6ACCD;">loadpulse run</span></span>
<span class="line"></span></code></pre></div>`,98),t=[i];function o(p,c,r,d,u,h){return a(),s("div",null,t)}const C=e(l,[["render",o]]);export{f as __pageData,C as default};
